def convert_chat_history_to_messages(last_25_chat_history):
    """
    Convert chat history to a list of HumanMessage and AIMessage objects.

    Args:
        last_25_chat_history: List of chat history pairs

    Returns:
        List of message objects (HumanMessage and AIMessage)
    """
    chat_history_msgs = []

    if not last_25_chat_history:
        debug("No chat history provided")
        return chat_history_msgs

    for i, pair in enumerate(last_25_chat_history):
        try:
            if isinstance(pair, dict) and "question" in pair and "answer" in pair:
                chat_history_msgs.append(HumanMessage(content=pair["question"]))
                chat_history_msgs.append(AIMessage(content=pair["answer"]))
            else:
                warning(f"Invalid chat history pair at index {i}: {pair}")
        except Exception as e:
            error(f"Error processing chat history pair at index {i}: {str(e)}")
            continue

    debug(f"Prepared {len(chat_history_msgs)} chat history messages")
    return chat_history_msgs


@try_catch_log("search button processing", include_locals=True)
def handle_search_process(search_query, original_query):
    """Handle the entire search process with comprehensive error tracking"""
    info(f"Search button clicked with query: '{search_query}'")

    # Initialize session with error handling
    try:
        initialize_chat_history()
        last_question = get_last_question()
        last_25_chat_history = get_last25_main_chat_history()
        debug(f"Session initialized successfully. Last question: {last_question}")
        debug_json(last_25_chat_history, "Last_25_Chat_History")
    except Exception as e:
        error(f"Session initialization failed: {str(e)}")
        st.error("Failed to initialize session. Please try again.")
        return None

    # Prepare chat history messages
    try:
        chat_history_msgs = convert_chat_history_to_messages(last_25_chat_history)
    except Exception as e:
        error(f"Error preparing chat history messages: {str(e)}")
        chat_history_msgs = []

    # Central agent processing with detailed error handling
    try:
        with st.spinner("Manager Agent is analyzing your query"):
            central_agent_response, business_keyword, analysis_score = asyncio.run(
                run_both_0_stage(
                    original_query=original_query,
                    search_query=search_query,
                    chat_history_msgs=chat_history_msgs,
                    chat_history_dict=last_25_chat_history,
                    PRODUCTS=PRODUCTS,
                )
            )
            debug(f"Analysis Score {analysis_score}")
            info(f"Business Keyword {business_keyword}")
        debug_json(central_agent_response, "Central_Agent_Response")
    except Exception as e:
        error(f"Central agent processing failed: {str(e)}")
        st.error(f"Central agent analysis failed: {str(e)}")
        return None

    # Extract and validate central agent response
    try:
        if not isinstance(central_agent_response, dict):
            error(
                f"Central agent response is not a dict: {type(central_agent_response)}"
            )
            st.error("Invalid response format from central agent")
            return None

        central_score = central_agent_response.get("score", 1)
        company_names = central_agent_response.get("company_names", [])
        central_location = central_agent_response.get("location", "")
        central_category = central_agent_response.get("category", "")

        # Validate company_names
        if company_names is None:
            warning("company_names is None, setting to empty list")
            company_names = []
        elif not isinstance(company_names, list):
            warning(
                f"company_names is not a list: {type(company_names)}, converting to list"
            )
            company_names = [company_names] if company_names else []

        info(f"Central Agent Score: {central_score}, Company Names: {company_names}")

    except Exception as e:
        error(f"Error extracting central agent response data: {str(e)}")
        st.error("Failed to process central agent response")
        return None

    return (
        central_score,
        company_names,
        last_question,
        business_keyword,
        analysis_score,
        central_location,
        central_category,
    )


