def create_streaming_function(text_content):
    """Create a streaming function for the given text content"""

    def stream_text_markdown():
        """Generator function to stream text word by word with proper markdown rendering"""
        import time
        import re

        # Get the original text
        cleaned_text = text_content

        # Step 1: Convert \n to actual newlines for proper markdown formatting
        cleaned_text = cleaned_text.replace("\\n", "\n")

        # Step 2: Clean up any remaining HTML-style breaks but preserve newlines
        cleaned_text = re.sub(r"<br\s*/?>", "\n", cleaned_text)

        # Step 3: Normalize multiple consecutive newlines (max 2 for markdown spacing)
        cleaned_text = re.sub(r"\n{3,}", "\n\n", cleaned_text)

        # Step 4: Remove any remaining HTML tags
        cleaned_text = re.sub(r"<[^>]+>", "", cleaned_text)

        # Step 5: Clean up bullet points - ensure proper markdown format
        lines = cleaned_text.split("\n")
        formatted_lines = []

        for line in lines:
            line = line.strip()
            if line:
                # Fix bullet points - ensure they start with "- " for markdown
                if line.startswith("-") and not line.startswith("- "):
                    line = "- " + line[1:].strip()
                formatted_lines.append(line)
            else:
                # Preserve empty lines for spacing
                formatted_lines.append("")

        # Rejoin with proper newlines
        cleaned_text = "\n".join(formatted_lines)

        # Step 6: Final cleanup of extra spaces within lines
        lines = cleaned_text.split("\n")
        final_lines = []
        for line in lines:
            if line.strip():
                # Clean up multiple spaces within the line but preserve structure
                line = re.sub(r" +", " ", line)
                final_lines.append(line)
            else:
                final_lines.append("")  # Keep empty lines for spacing

        cleaned_text = "\n".join(final_lines)

        # Now stream word by word while preserving line structure
        words = []
        lines = cleaned_text.split("\n")

        for i, line in enumerate(lines):
            if line.strip():  # If line has content
                line_words = line.split()
                for word in line_words:
                    words.append(word)
                # Add newline marker after each line (except the last one)
                if i < len(lines) - 1:
                    words.append("\n")
            else:  # Empty line - preserve it
                words.append("\n")

        # Stream the words
        current_text = ""
        for word in words:
            if word == "\n":
                current_text += "\n"
            else:
                current_text += word + " "

            yield current_text
            time.sleep(0.03)  # Slightly faster streaming

    return stream_text_markdown


